class App extends Component {
  state = {
    open: false,
  };
  handleButtonClick = () => {
    this.setState((state) => {
      return {
        open: !state.open,
      };
    });
  };
}
container = React.createRef();
state = {
  open: false,
};
handleClickOutside = (event) => {
  if (
    this.container.current &&
    !this.container.current.contains(event.target)
  ) {
    this.setState({
      open: false,
    });
  }
}
export default Language_toggle;
