import React from 'react';
import world_img from '../images/world_img.png'
import './Language_btn.css'
class dropbtn extends Component {
  state = {
    open: false,
  };
  handleButtonClick = () => {
    this.setState((state) => {
      return {
        open: !state.open,
      };
    });
  };
}
const btn_lng = (c) => {
	return (
		<div className="dropbtn">
			<div className="container">
				<button type="button" class="button">
				<img src={world_img} alt="lng" />
				</button>
			</div>
			<div class="dropdown">
    <ul>
      <li>Option 1</li>
      <li>Option 2</li>
    </ul>
  </div>
		</div>
	);
	
}


export default btn_lng;
