import React from 'react'
import './Navigation.css'
import Logo from './Logo'
import en from '../images/en.png'
import ru from '../images/ru.png'
import { useTranslation } from 'react-i18next';


const Navigation = () => {
	const { t, i18n } = useTranslation();
	const changeLanguage = (language) => {
  i18n.changeLanguage(language);
	};
	return (
		<nav className='Navbar'>
			<button onClick={() => changeLanguage("en")}>EN</button>
      <button onClick={() => changeLanguage("ru")}>RU</button>
			<Logo />
			<a href='#' className='Artificialintelligence'>{t()}</a>
			<a href='#' className='Home'>Home</a>
			<a href='#' className='About'>About</a>
			<a href='#' className='Services'>Services</a>
			<a href='#' className='Blog'>Blog</a>
			<a href='#' className='Contact'>Contact</a>
			<div className='England'><a href='http://localhost:3000/?lng=en'><img src={en} alt="en" /></a></div>
			<div className='Russia'><a href='http://localhost:3000/?lng=ru'><img src={ru} alt="ru" /></a></div>
			<btn className='Log_in'><a href=''> Sign In</a></btn>	
		</nav>
	)
}

export default Navigation;
