import React from 'react';
import world_img from '../images/world_img.png'
import './Language_btn.css'


const Language_btn = () => {
	return (
		class Language_btn extends c {
			state = {
				open: false,
			};
			handleButtonClick = () => {
				this.setState((state) => {
					return {
						open: !state.open,
					};
				});
			};
		},
		<div className="dropbtn">
			<div className="container" ref={this.container}>
				<button type="button" class="button" onClick={this.handleButtonClick}>
				<img src={world_img} alt="lng" />
				</button>
			</div>
			{this.state.open && (
			<div class="dropdown">
    <ul>
      <li>Option 1</li>
      <li>Option 2</li>
    </ul>
  </div>
			)}
		</div>
	);
	
}
export default Language_btn;
